import React, { useEffect, useState } from "react";
import Card from "./Card";
import Pokinfo from "./Pokinfo";
import axios from "axios";

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState("https://pokeapi.co/api/v2/pokemon/");
  const [nextUrl, setNextUrl] = useState();
  const [prevUrl, setPrevUrl] = useState();

  const pokeFun = async () => {
    setLoading(true);
    const res = await axios.get(url);
    setNextUrl(res.data.next);
    setPrevUrl(res.data.previous);
    getPokemon(res.data.results);
    setLoading(false);
  };

  const getPokemon = async (res) => {
    const uniquePokemon = [];
    for (const item of res) {
      try {
        const result = await axios.get(item.url);
        const pokemon = result.data;
        if (!pokeData.find((p) => p.id === pokemon.id)) {
          uniquePokemon.push(pokemon);
        }
      } catch (error) {
        console.error(error);
      }
    }
    
    // Combine the unique Pokémon data with the existing data
    const combinedData = [...pokeData, ...uniquePokemon];

    // Sort the combined data based on the 'id' property
    combinedData.sort((a, b) => a.id - b.id);

    setPokeData(combinedData);
  };

  useEffect(() => {
    pokeFun();
  }, [url]);

  return (
    <>
      <div className="container">
        <div className="left-content">
          <Card pokemon={pokeData} loading={loading} />
          <div className="btn-group">
            <button>Previous</button>
            <button>Next</button>
          </div>
        </div>
        <div className="right-content">
          <Pokinfo />
        </div>
      </div>
    </>
  );
};

export default Main;
